	void MyResize_warpAffine(cv::Mat src, cv::Mat &dst)
	{
		int np = 10;
		int w = dst.cols;
		int h = dst.rows;
		int* px = gXX;
		int* py = gYY;

		// #pragma omp parallel for num_threads(4)
		for (int i=0; i < h; i++)
		{
			uchar* q = dst.ptr<uchar>(i);
			#pragma omp parallel for num_threads(4)
			for (int j=0; j < w; j++)
				q[j] = src.at<uchar>(py[j], px[j]);
			px += w;
			py += w;
		}
	}

